// 直接导入地理图所需的json文件，注意一定记得register这个文件
import worldJson from 'echarts/map/json/world.json'
import $search from "./../../util/search.js"
import $drawGraphs from "./../../util/drawGraphs.js"

let echarts = require('echarts');
require('echarts-wordcloud');

export default {
    name: "wordcloud",
    props: {
        items: {
            type: Array,
            default () {
                return {
                    "test": "test"
                }
            }
        },
        keyString:{
            type:String,
            default(){
                return "test"
            }
        }
    },
    data() {
        return {
            // tabItem:"Paper"
        }
    },
    mounted() {
        this.start_draw();
    },
    // watch:{
    //     items:function() {
    //         // this.draw_linefold();
    //         console.log("watch items:"+ items);
    //     },
    //     deep:true
    // },
    // updated:function() {
    //     this.$nextTick(function(){
    //         console.log("updated!");
    //         this.start_draw();
    //     })
    // },
    methods: {
        start_draw() {
            let id = 'wordcloud'+'_'+ this.keyString;
            this.draw_wordcloud(id,this.items);
        },
        draw_wordcloud(id,data) {
            let chart = document.getElementById(id);
            let myChart = echarts.init(chart);

            // console.log(JSON.stringify(data))
            const option = {
                // title: {
                //     text: title,
                //     top: 'top',
                //     // left: 'center'
                // },
                tooltip: {
                    trigger: 'item',
                    // formatter:
                },
                toolbox: {
                    show: false,
                    orient: 'vertical',
                    x: 'right',
                    y: 'center',
                },
                    series: [{
                    name: '词云',
                    type: 'wordCloud',
                    size: ['90%', '90%'],
                    textRotation : [-90, 90],
                    shape: 'circle',
                    autoSize: {
                        enable: true,
                        minSize: 14
                    },
                    textStyle:{
                        normal:{
                            color: (params, index) => {
                                return $drawGraphs.set_color()
                            }
                            // color:function () {
                            //     return 'rgb(' + [
                            //         Math.round(Math.random() * 160),
                            //         Math.round(Math.random() * 160),
                            //         Math.round(Math.random() * 160)
                            //         ].join(',') + ')';
                            // }
                        },
                        emphasis:{
                            shadowBlur:10,
                            shadowColor:'#999'
                        }
                    },
                    data: data
                }]

            };

            myChart.setOption(option);

            // 生成多个图时实现全部都能自适应。参考博客：https://blog.csdn.net/qq_25816185/article/details/82414529
            window.addEventListener('resize', function () {
                // initial();
                myChart.resize()
            })

            myChart.on("click",function(params){
                // $router.push({
                //     path:"/displayInfo",
                //     query:{query:params['data']['name'].toLowerCase()}
                // })
                let query = params['data']['name'].toLowerCase()
                $search.to_display(query)
            })
        }
    }
}
